import React, { useState, useEffect } from 'react';

const CreateItemForm = () => {
  const [categories, setCategories] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category_id: '',
    price: '',
    images: '',
    user_id: '', // New field for user_id
  });

  useEffect(() => {
    fetchCategories();
    const userId = getCookie('user_id'); // Get user_id from cookie
    setFormData({ ...formData, user_id: userId }); // Set user_id in formData
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:5001/api/v1/categories');
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch('http://localhost:5001/api/v1/items', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => response.json())
      .then((data) => console.log(data))
      .catch((error) => console.log(error));
  };

  // Helper function to get cookie value by name
  const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
  };

  return (
    <div className="container mt-4">
      <h2>Create Item</h2>
      <form onSubmit={handleSubmit}>
        {/* Rest of the form fields */}
        <div className="mb-3">
          <label htmlFor="price" className="form-label">
            user_id:
          </label>
          <input
            type="number"
            className="form-control"
            id="user_id"
            name="user_id"
            value={formData.user_id}
            onChange={handleChange}
            required
          />
        </div>
        {/* ... */}
        <button type="submit" className="btn btn-primary">
          Create Item
        </button>
      </form>
    </div>
  );
};

export default CreateItemForm;
